// Four.js

import React from 'react';
import { Container, Typography, Button } from '@mui/material';

const Four = () => {
  const handleReadMoreClick = () => {
    // Add logic to handle opening the presentation link
    // For demonstration purposes, you can redirect to the link or open it in a new tab
    window.open('https://docs.google.com/presentation/d/1KxnP-F73ZksoV0TQTopgqpzHQ2er-L73/edit?usp=sharing&ouid=102912823287222338486&rtpof=true&sd=true', '_blank');
  };

  return (
    <Container>
      <Typography variant="h4" gutterBottom>
        Lesson Plan Development
      </Typography>

      <Typography>
        Crafting impactful lessons involves the ability of educators to seamlessly integrate multiple subjects, fostering connections across diverse topics and enhancing engagement. Discover how educators can achieve this by incorporating writing across the curriculum by clicking "Read More" to explore effective classroom practices!
      </Typography>

      <Button variant="contained" color="primary" onClick={handleReadMoreClick}>
        Read More
      </Button>

      <Typography variant="h6" style={{ marginTop: '2rem' }}>
        Integrated Curriculum Presentation
      </Typography>

      {/* Embed the PowerPoint presentation here */}
      <iframe
        title="Integrated Curriculum Presentation"
        src="https://docs.google.com/presentation/d/e/2PACX-1vScY0l3A6hvDCo7cIhB4E4qFePtUcWJ2mggYK2E2vA0EFXkBX-FivhNX1A8DxhCJk2ZR-0n6XE2wEII/embed?start=false&loop=false&delayms=3000"
        width="100%"
        height="400"
        allowfullscreen="true"
        mozallowfullscreen="true"
        webkitallowfullscreen="true"
      ></iframe>
    </Container>
  );
}

export default Four;
