// Banner.js

import React from 'react';
import YouTube from 'react-youtube';

const Banner = () => {
  const videoId = 'dg58bgNU-1Y';

  // Styling for the content on top of the video
  const contentStyle = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    textAlign: 'center',
    color: 'white',
    zIndex: 1,
    textShadow: '2px 2px 4px rgba(0, 0, 0, 0.7)',
  };

  // Styling for the video
  const videoOptions = {
    height: '100%',
    width: '100%',
    playerVars: {
      autoplay: 1,
      mute: 1,
      loop: 1,
      playlist: videoId,
      controls: 0,
      showinfo: 0,
      modestbranding: 1,
      fs: 0,
    },
  };

  return (
    <div style={{ position: 'relative', overflow: 'hidden', height: '100vh', width: '100vw' }}>
      {/* Embed YouTube video */}
      <YouTube videoId={videoId} opts={videoOptions} />

      {/* Content on top of the video */}
      <div style={contentStyle}>
        <div id="banner">
          <div style={{ fontSize: '3rem', marginBottom: '1rem', background: 'transparent' }}>CI6103</div>
          <div style={{ fontSize: '2rem', marginBottom: '1rem', background: 'transparent' }}>Curriculum and Instruction Design for Diversity</div>
          <div style={{ fontSize: '1.5rem', background: 'transparent' }}>Sharing Action Research Using Technology</div>
        </div>
      </div>
    </div>
  );
};

export default Banner;
