import React from 'react';
import { Card, CardContent, Typography, Button } from '@mui/material';
import { makeStyles } from '@mui/styles';

const useStyles = makeStyles((theme) => ({
  readMoreCard: {
    backgroundColor: theme.palette.background.paper, // You can customize the background color
    padding: theme.spacing(3),
    borderRadius: theme.spacing(1),
    boxShadow: theme.shadows[2], // You can customize the shadow
  },
  title: {
    marginBottom: theme.spacing(2),
  },
  content: {
    marginBottom: theme.spacing(2),
  },
  button: {
    backgroundColor: theme.palette.primary.main, // You can customize the button color
    color: theme.palette.primary.contrastText, // You can customize the button text color
    '&:hover': {
      backgroundColor: theme.palette.primary.dark, // You can customize the hover color
    },
  },
}));

const CustomCard = (props) => {
  const classes = useStyles();

  return (
    <Card id="r4" className={classes.readMoreCard}>
      <CardContent>
        <Typography variant="h5" className={classes.title}>
          {props.title}
        </Typography>
        <Typography variant="body1" className={classes.content}>
            {props.content}
        </Typography>
        <Button variant="contained" className={classes.button}>
          Read More
        </Button>
      </CardContent>
    </Card>
  );
};

export default CustomCard;
